$(document).ready(function(){
/*! Loading */
function ShowLoading(a){$("#loading-layer").remove();$("body").append("<div id='loading-layer' style='display:none'></div>");a?$("#loading-layer").html(a):$("#loading-layer").html();a=($(window).width()-$("#loading-layer").width())/2;var b=($(window).height()-$("#loading-layer").height())/2;$("#loading-layer").css({left:a+"px",top:b+"px",position:"fixed",zIndex:"9999"});$("#loading-layer").fadeTo("slow",.6)}
function HideLoading(a){$("#loading-layer").fadeOut("slow",function(){$("#loading-layer").remove()})}
/*! DLEdialog */
function DLEalert(a,b){$("#dlepopup").remove();$("body").append("<div id='dlepopup' class='dle-alert' title='"+b+"' style='display:none'>"+a+"</div>");$("#dlepopup").dialog({autoOpen:!0,width:470,resizable:!1,dialogClass:"modalfixed dle-popup-alert",buttons:{OK:function(){$(this).dialog("close");$("#dlepopup").remove()}}});$(".modalfixed.ui-dialog").css({position:"fixed"});$("#dlepopup").dialog("option","position",["0","0"])}
var message_info='Информация';
/*! AjaxQuery */
$("#auth").click(function(){var e=$.trim($("#username").val()),a=$.trim($("#password1").val());ShowLoading(""),$.ajax({type:"POST",url:"login.php",data:{user_login:e,user_password1:a},success:function(e){e[0]?(HideLoading(""),window.location.href="//24myfree.ru/admin/panel/"):(HideLoading(""),DLEalert(e[2],message_info))},dataType:"json"})});
});